package chapter02;

public class Variable1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 변수
		// 변수를 선언하고, 값을 대입(저장)하고, 출력하는 프로그램
		int level; // 변수자료형예약어 변수이름; int -> 정수형
		level = 10;	// level변수에 정수10을 대입(저장)했습니다.
		System.out.println(level); // 화면에 출력하는 함수
	}

}
