package chapter02;

public class Variable2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 변수선언시 초기값을 주고, 화면에 변수값을 출력하는 프로그램
		int level = 10;	// 자료형 변수이름 = 초기값;
		System.out.println(level);
		// 변수이름
		// 영문자, 숫자, _, $
		// 숫자로 시작하는 변수명은 사용할 수 없습니다.
		// Java에서는 Camel 표기법을 변수이름으로 가장많이 사용합니다.
		// Java의 변수 -> 자료형
		// 1. 정수형 : byte(1), short(2), int(4), long(8)
		// 2. 실수형 : float(4), double(8)
		// 3. 문자형 : char
		// 4. 논리형 : boolean
	}

}
